mapper.select:
SELECT cms_product.cms_product_id, cms_product.name, cms_product.product_category_id, cms_product.ctime, cms_product.mtime, cms_product.status, cms_product.xt_userinfo_id, cms_product.imgpath, xt_userinfo.xt_userinfo_realName FROM cms_product cms_product LEFT JOIN xt_userinfo xt_userinfo ON cms_product.xt_userinfo_id = xt_userinfo.xt_userinfo_id WHERE 1=1
AND cms_product.name LIKE CONCAT(CONCAT('%',#{name}),'%')
AND cms_product.ctime >=#{ctime_st}
AND cms_product.ctime <=#{ctime_et}
